package com.aslan.pay.controller;import com.aslan.pay.model.Category;import com.aslan.pay.payload.CategoryRequest;import com.aslan.pay.service.CategoryService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/category")public class CategoryController {    @Autowired    CategoryService categoryService;    @PostMapping    public ResponseEntity<Category> saveCategory(@RequestBody CategoryRequest request) {        Category categoryEntity = Category.builder()                .image(request.getImage())                .name(request.getName()).build();        Category category = categoryService.saveCategory(categoryEntity);        return ResponseEntity.ok(category);    }    @GetMapping    public ResponseEntity<List<Category>> getMerchants() {        List<Category> categoryList = categoryService.getCategories();        return ResponseEntity.ok(categoryList);    }    @DeleteMapping("{id}")    public ResponseEntity<HttpStatus> deleteCategory(@PathVariable("id") int id) {        categoryService.deleteCategory(id);        return ResponseEntity.ok(HttpStatus.OK);    }}