package com.aslan.pay.controller;import com.aslan.pay.model.Category;import com.aslan.pay.payload.CategoryRequest;import com.aslan.pay.service.CategoryService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;import java.util.stream.Collectors;@RestController@RequestMapping("/api/category")public class CategoryController {    @Autowired    CategoryService categoryService;    @PostMapping    public ResponseEntity<List<Category>> saveCategory(@RequestBody List<CategoryRequest> requests) {        List<Category> categoryList = requests.stream()                .map(item -> Category.builder()                        .image(item.getImage())                        .name(item.getName())                        .categoryId(item.getCategoryId()).build()).collect(Collectors.toList());        return ResponseEntity.ok(categoryService.saveCategory(categoryList));    }    @GetMapping    public ResponseEntity<List<Category>> getCategory() {        List<Category> categoryList = categoryService.getCategories();        return ResponseEntity.ok(categoryList);    }}