package com.aslan.pay.controller;import com.aslan.pay.model.Merchant;import com.aslan.pay.payload.MerchantRequest;import com.aslan.pay.service.MerchantService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/merchant")public class MerchantController {    @Autowired    MerchantService merchantService;    @PostMapping    public ResponseEntity<Merchant> saveMerchant(@RequestBody MerchantRequest request) {        Merchant merchantEntity = Merchant.builder()                .time(request.getTime())                .link(request.getLink())                .cashback(request.getCashback())                .img(request.getImg())                .name(request.getName())                .type(request.getType()).build();        Merchant merchant = merchantService.saveMerchant(merchantEntity);        return ResponseEntity.ok(merchant);    }    @GetMapping    public ResponseEntity<List<Merchant>> getMerchants() {        List<Merchant> merchantList = merchantService.getMerchants();        return ResponseEntity.ok(merchantList);    }    @DeleteMapping("/{id}")    public ResponseEntity<HttpStatus> deleteMerchant(@PathVariable("id") int id) {        merchantService.deleteMerchant(id);        return ResponseEntity.ok(HttpStatus.OK);    }}