package com.aslan.pay.controller;import com.aslan.pay.model.Merchant;import com.aslan.pay.payload.MerchantRequest;import com.aslan.pay.service.MerchantService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;import java.util.stream.Collectors;@RestController@RequestMapping("/api/merchant")public class MerchantController {    @Autowired    MerchantService merchantService;    @PostMapping    public ResponseEntity<List<Merchant>> saveMerchant(@RequestBody List<MerchantRequest> requests) {        List<Merchant> merchantList = requests.stream()                .map(item -> Merchant.builder()                        .time(item.getTime())                        .link(item.getLink())                        .cashback(item.getCashback())                        .img(item.getImg())                        .name(item.getName())                        .categoryId(item.getCategoryId()).build()).collect(Collectors.toList());        return ResponseEntity.ok(merchantService.saveMerchant(merchantList));    }    @GetMapping("/{categoryId}")    public ResponseEntity<List<Merchant>> getMerchants(@PathVariable("categoryId") int categoryId, @RequestParam(value = "searchTerm", defaultValue = "") String searchTerm) {        List<Merchant> merchantList = merchantService.getMerchants(categoryId, searchTerm);        return ResponseEntity.ok(merchantList);    }    @DeleteMapping("/{id}")    public ResponseEntity<HttpStatus> deleteMerchant(@PathVariable("id") int id) {        merchantService.deleteMerchant(id);        return ResponseEntity.ok(HttpStatus.OK);    }}