package com.aslan.pay.model;import jakarta.persistence.*;import lombok.*;import java.io.Serializable;@Builder@Setter@Getter@NoArgsConstructor@AllArgsConstructor@Data@Table(name = "category")@Entitypublic class Category implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    @Column(name = "category_id")    private int categoryId;    @Column(name = "name")    private String name;    @Column(name = "image")    private String image;}