package com.aslan.pay.repository;import com.aslan.pay.model.Merchant;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface MerchantRepository extends JpaRepository<Merchant, Long> {    @Query("SELECT m FROM Merchant m WHERE " +            "((:categoryId > 0 AND m.categoryId = :categoryId) OR :categoryId = 0) " +            "AND LOWER(m.name) LIKE LOWER(CONCAT('%', :searchTerm, '%'))")    List<Merchant> findMerchantsByCategoryIdAndNameContaining(@Param("categoryId") int categoryId, @Param("searchTerm") String searchTerm);}