package com.aslan.pay.service;import com.aslan.pay.model.Category;import com.aslan.pay.repository.CategoryRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class CategoryService {    @Autowired    CategoryRepository categoryRepository;    public Category saveCategory(Category category) {        return categoryRepository.save(category);    }    public List<Category> getCategories() {        return categoryRepository.findAll();    }    public void deleteCategory(long id) {        categoryRepository.deleteById(id);    }}