package com.aslan.pay.service;import com.aslan.pay.model.Category;import com.aslan.pay.repository.CategoryRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.UUID;import static java.util.UUID.randomUUID;@Servicepublic class CategoryService {    @Autowired    CategoryRepository categoryRepository;    public List<Category> saveCategory(List<Category> categoryList) {        return categoryRepository.saveAll(categoryList);    }    public List<Category> getCategories() {        return categoryRepository.findAll();    }    public void deleteCategory(long id) {        categoryRepository.deleteById(id);    }}