package com.aslan.pay.service;import com.aslan.pay.model.Merchant;import com.aslan.pay.repository.MerchantRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class MerchantService {    @Autowired    MerchantRepository merchantRepository;    public Merchant saveMerchant(Merchant merchant) {        return merchantRepository.save(merchant);    }    public List<Merchant> getMerchants() {        return merchantRepository.findAll();    }    public void deleteMerchant(long id) {        merchantRepository.deleteById(id);    }}