package com.aslan.pay.service;import com.aslan.pay.model.Merchant;import com.aslan.pay.repository.MerchantRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import static java.util.UUID.randomUUID;@Servicepublic class MerchantService {    @Autowired    MerchantRepository merchantRepository;    public List<Merchant> saveMerchant(List<Merchant> merchantList) {        return merchantRepository.saveAll(merchantList);    }    public List<Merchant> getMerchants(int categoryId, String searchTerm) {        return merchantRepository.findMerchantsByCategoryIdAndNameContaining(categoryId,searchTerm);    }    public void deleteMerchant(long id) {        merchantRepository.deleteById(id);    }}